                        int vIdx;
                        for (vIdx = 0; vIdx < nItems; vIdx++) {
                            if (MWMCM[vIdx] == newMatchedBidIdx) {
                                break;
                            }
                        }
                        // loop through all v_1 to check violation of rule 2
